import '../App.css';
import React from "react";
import { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import axios from "axios";
import logo from "../components/back.png";

const Show = () => {

    const navigate = useNavigate();

    const [selectedDateStart, setSelectedDateStart] = useState("");
    const [selectedDateStop, setSelectedDateStop] = useState("");
    const [periodday, setPeriodday] = useState("");
    const [message, setMessage] = useState("");

    const handleLogin = () => {
        navigate("/login");
    };

    // const runRPA = async () => {
    //     try {
    //       const response = await axios.post("http://127.0.0.1:5000/run_rpa", {
    //         param1: tr,
    //         param2: td,
    //         param3: month1,
    //         param4: year1,
    //         param5: periodday,
    //       });
    
    //       setMessage(response.data.message);
    //     } catch (error) {
    //       console.error("Error:", error);
    //       setMessage("Failed to start RPA");
    //     }
    // };

    // def get_column_day(selected_date):
    //     weekday = selected_date.weekday()
    //     weekday = (weekday + 1) % 7
    //     column = (weekday % 7) + 1
    //     day = selected_date.day
    //     return day, column

    // def get_row_day(day, column, selected_date):
    //     first_day_of_month = selected_date.replace(day=1)
    //     first_day_column = ((first_day_of_month.weekday() + 1) % 7) + 1
    //     day_position = day + (first_day_column - 1)
    //     row = (day_position - 1) // 7 + 1
    //     return row

    const splitDate = (dateString1, dateString2) => {
        const [year1, month1, day1] = dateString1.split("-");
        const [year2, month2, day2] = dateString2.split("-")
        return { year1, month1, day1, day2};
    };

    function getColumnDay(selectedDate) {
        const dateObj = new Date(selectedDate); // à¹à¸›à¸¥à¸‡à¹€à¸›à¹‡à¸™ Date object
        let weekday = dateObj.getDay(); // à¸«à¸²à¸§à¸±à¸™à¸—à¸µà¹ˆ (0=à¸­à¸²à¸—à¸´à¸•à¸¢à¹Œ, 6=à¹€à¸ªà¸²à¸£à¹Œ)
        
        weekday = (weekday + 6) % 7; // à¸›à¸£à¸±à¸šà¹ƒà¸«à¹‰ 0=à¸ˆà¸±à¸™à¸—à¸£à¹Œ, 6=à¸­à¸²à¸—à¸´à¸•à¸¢à¹Œ
        const column = (weekday % 7) + 1; // à¸›à¸£à¸±à¸š column à¹ƒà¸«à¹‰à¹€à¸£à¸´à¹ˆà¸¡à¸—à¸µà¹ˆ 1
        const day = dateObj.getDate(); // à¸”à¸¶à¸‡à¸§à¸±à¸™à¸—à¸µà¹ˆ (1-31)
      
        return { day, column };
    }

    function getRowDay(day, column, selectedDate) {
        const dateObj = new Date(selectedDate);
        const firstDayOfMonth = new Date(dateObj.getFullYear(), dateObj.getMonth(), 1); // à¸§à¸±à¸™à¸—à¸µà¹ˆ 1 à¸‚à¸­à¸‡à¹€à¸”à¸·à¸­à¸™
        let firstDayColumn = ((firstDayOfMonth.getDay() + 6) % 7) + 1; // à¸«à¸²à¸§à¸±à¸™à¹à¸£à¸à¸‚à¸­à¸‡à¹€à¸”à¸·à¸­à¸™à¸­à¸¢à¸¹à¹ˆà¸„à¸­à¸¥à¸±à¸¡à¸™à¹Œà¹„à¸«à¸™
        
        const dayPosition = day + (firstDayColumn - 1); // à¸„à¸³à¸™à¸§à¸“à¸•à¸³à¹à¸«à¸™à¹ˆà¸‡à¸§à¸±à¸™à¹ƒà¸™ grid
        const row = Math.floor((dayPosition - 1) / 7) + 1; // à¸„à¸³à¸™à¸§à¸“ row à¸—à¸µà¹ˆà¸­à¸¢à¸¹à¹ˆ
        return row;
    }

    function getColumnAndRow(selectedDate) {
        if (!selectedDate) {
            console.error("âŒ Error: selectedDate is undefined or null");
            return undefined;
        }
    
        const dateObj = new Date(selectedDate);
        if (isNaN(dateObj.getTime())) {
            console.error(`âŒ Error: Invalid Date format - selectedDate: ${selectedDate}`);
            return undefined;
        }
    
        // ðŸ“Œ à¸„à¸³à¸™à¸§à¸“ column (à¸§à¸±à¸™à¹ƒà¸™à¸ªà¸±à¸›à¸”à¸²à¸«à¹Œ: à¸ˆà¸±à¸™à¸—à¸£à¹Œ=1, à¸­à¸²à¸—à¸´à¸•à¸¢à¹Œ=7)
        let weekday = (dateObj.getDay() + 6) % 7 + 1;
        let column = weekday; // à¸„à¸­à¸¥à¸±à¸¡à¸™à¹Œà¸—à¸µà¹ˆà¸§à¸±à¸™à¸™à¸±à¹‰à¸™à¸­à¸¢à¸¹à¹ˆ (1 = à¸ˆà¸±à¸™à¸—à¸£à¹Œ, 7 = à¸­à¸²à¸—à¸´à¸•à¸¢à¹Œ)
        let day = dateObj.getDate(); // à¸§à¸±à¸™à¸—à¸µà¹ˆà¹ƒà¸™à¹€à¸”à¸·à¸­à¸™
    
        // ðŸ“Œ à¸„à¸³à¸™à¸§à¸“ row
        const firstDayOfMonth = new Date(dateObj.getFullYear(), dateObj.getMonth(), 1);
        let firstDayColumn = ((firstDayOfMonth.getDay() + 6) % 7) + 1; // à¸«à¸²à¸§à¸±à¸™à¹à¸£à¸à¸‚à¸­à¸‡à¹€à¸”à¸·à¸­à¸™à¸­à¸¢à¸¹à¹ˆà¸„à¸­à¸¥à¸±à¸¡à¸™à¹Œà¹„à¸«à¸™
        let dayPosition = day + (firstDayColumn - 1);
        let row = Math.ceil(dayPosition / 7); // à¸«à¸²à¸§à¹ˆà¸²à¸­à¸¢à¸¹à¹ˆà¹à¸–à¸§à¸—à¸µà¹ˆà¹€à¸—à¹ˆà¸²à¹„à¸«à¸£à¹ˆ
    
        console.log(`âœ… Debugging - selectedDate: ${selectedDate}`);
        console.log(`âœ… Debugging - day: ${day}, column: ${column}, row: ${row}`);
    
        return { day, column, row };
    }

    const show = () => {
        // const { year1, month1, day1, day2} = splitDate(selectedDateStart,selectedDateStop);
        // const {day , column} = getColumnDay(selectedDateStart)
        setPeriodday(day2 - day1);
        // const {row} = getRowDay(day, column, selectedDateStart)
        const {} = getColumnAndRow(selectedDateStart)
        alert(`periodday : ${periodday} , d=${day} , c=${column} , r=${row}`)
    };

    return (
        <div className="Containner-pull">
            <p>Standing Contract - Pull images</p>
            <p className='Text-date'>Day - Start</p>
            <input
                type="date"
                value={selectedDateStart}
                onChange={(e) => setSelectedDateStart(e.target.value)}
                min="2024-01-01" 
                max="2025-12-31" 
                className="Date-picker"
            />
            <p className='Text-date'>Day - Stop</p>
            <input
                type="date"
                value={selectedDateStop}
                onChange={(e) => setSelectedDateStop(e.target.value)}
                min="2024-01-01" 
                max="2025-12-31" 
                className="Date-picker"
            />
            <button
                className='Button-stylePull'
                onClick={show}
            >
            Pull Images
            </button>
            <div className='Div-back'>
            <Link to="/pull" className="logo">
                <img src={logo} alt="" className="Back-ele" />
            </Link>
            <p className='Back-text'>back</p>
            </div>
        </div>
    );
};

export default Show;
