import '../App.css';
import React from "react";
import { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import axios from "axios";
import logo from "../components/back.png";

const Show = () => {

    const navigate = useNavigate();

    const [selectedDateStart, setSelectedDateStart] = useState("");
    const [selectedDateStop, setSelectedDateStop] = useState("");
    const [periodday, setPeriodday] = useState("");
    const [message, setMessage] = useState("");

    const handleLogin = () => {
        navigate("/login");
    };

    const runRPA = async () => {
        try {
          const response = await axios.post("http://127.0.0.1:5000/run_rpa", {
            param1: tr,
            param2: td,
            param3: month1,
            param4: year1,
            param5: periodday,
          });
    
          setMessage(response.data.message);
        } catch (error) {
          console.error("Error:", error);
          setMessage("Failed to start RPA");
        }
    };

    const splitDate = (dateString1) => {
        const [year1, month1, day1] = dateString1.split("-");
        return { year1, month1, day1, year2, month2, day2};
    };

    const { year1, month1, day1, year2, month2, day2 } = splitDate(selectedDateStart , selectedDateStop);

    return (
        <div className="Containner-pull">
            <p>Standing Contract - Pull images</p>
            <p className='Text-date'>Day - Start</p>
            <input
                type="date"
                value={selectedDateStart}
                onChange={(e) => setSelectedDateStart(e.target.value)}
                min="2024-01-01" 
                max="2025-12-31" 
                className="Date-picker"
            />
            <p className='Text-date'>Day - Stop</p>
            <input
                type="date"
                value={selectedDateStop}
                onChange={(e) => setSelectedDateStop(e.target.value)}
                min="2024-01-01" 
                max="2025-12-31" 
                className="Date-picker"
            />
            <button
                className='Button-stylePull'
                // onClick={showdate}
            >
            Pull Images
            </button>
            <div className='Div-back'>
            <Link to="/pull" className="logo">
                <img src={logo} alt="" className="Back-ele" />
            </Link>
            <p className='Back-text'>back</p>
            </div>
        </div>
    );
};

export default Show;
