import '../App.css';
import React from "react";
import { useState, useEffect, useMemo } from "react";
import { useNavigate, Link } from "react-router-dom";
import axios from "axios";
import logo from "../components/back.png";


const ShowST = () => {

    const navigate = useNavigate();

    const [image, setImage] = useState(null);
  const [prediction, setPrediction] = useState("");
  const [imagesList, setImagesList] = useState([]);
  const [dates, setDates] = useState({ datestart: "", datestop: "" });

  const handleImageChange = (e) => {
    setImage(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!image) {
      alert("Please select an image first.");
      return;
    }

    const formData = new FormData();
    formData.append("file", image);

    try {
      const response = await axios.post("http://127.0.0.1:5000/classify", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      setPrediction(`Prediction: ${response.data.prediction}`);
    } catch (error) {
      console.error("Error uploading file:", error);
    }
  };

  const handleDateChange = (e) => {
    setDates({ ...dates, [e.target.name]: e.target.value });
  };

  const fetchImages = async () => {
    try {
      const response = await axios.post("http://127.0.0.1:5000/predict", dates, {
        headers: { "Content-Type": "application/json" },
      });
      setImagesList(response.data.images);
    } catch (error) {
      console.error("Error fetching images:", error);
    }
  };

    return (
        <div className="Containner-showdis">
            {/* <h2>ผลลัพธ์จาก AI Model</h2>
            <button onClick={fetchPredictions}>โหลดผลลัพธ์ใหม่</button>
            <div style={{ display: "flex", flexWrap: "wrap" }}>
                {images.map((img, index) => (
                    <div key={index} style={{ margin: 10, textAlign: "center" }}>
                        <img src={img.path} alt={img.filename} width="150" />
                        <p><b>{img.filename}</b></p>
                        <p>Prediction: {img.prediction}</p>
                    </div>
                ))}
            </div> */}
            <p className='Text-H'>Prediction</p>
            <button
                className='Button-stylePredict'
                onClick={fetchMessage}
            >
            Predict!
            </button>
            <div className='Div-pic'>
                {images.map((img, index) => (
                    <div key={index} className='Div-Pre-Text'>
                        <img src={img.path} alt={img.filename} className='image' />
                        <p><b>{img.filename}</b></p>
                        <p>Prediction: {img.prediction}</p>
                    </div>
                ))}
            </div>
            <div className='Div-back'>
            <Link to="/show" className="logo">
                <img src={logo} alt="" className="Back-ele" />
            </Link>
            <p className='Back-text'>back</p>
            </div>
        </div>
    );
};

export default ShowST;
