import '../App.css';
import React from "react";
import { useState, useEffect, useMemo } from "react";
import { useNavigate, Link } from "react-router-dom";
import axios from "axios";
import logo from "../components/back.png";


const ShowST = () => {

    const navigate = useNavigate();
    const [imageBase64, setImageBase64] = useState([]);
    const [images, setImages] = useState([]);

    // useEffect(() => {
    //     fetch("http://localhost:5000/sendimages")
    //         .then((res) => res.json())
    //         .then((data) => setImageBase64(data.images));
    // }, []);

    useEffect(() => {
        fetch("http://127.0.0.1:5000/get-images")
          .then((res) => res.json())
          .then((data) => setImages(data || [])) // ✅ ถ้า `data` เป็น `null` หรือ `undefined` ให้ใช้ `[]`
          .catch((err) => {
            console.error("Error fetching images:", err);
            setImages([]); // ✅ ถ้ามี error ให้ตั้งเป็นอาร์เรย์ว่าง
          });
      }, []);

    return (
        <div className="Containner-showdis" >
            <p className='Text-show'>Prediction</p>
            <div className='Div-img-con'>
                {/* {imageBase64.map((src, index) => (
                    <div key={index} className='Div-img-box' style={{ display: 'flex', flexDirection: 'column', justifyContent: 'space-between', marginRight:'10px'}}>
                        <img src={src} alt={`Flask Image ${index}`} className='Image-style' />
                        <div key={index} style={{ background: 'gray', borderBottomLeftRadius: '30px',  borderBottomRightRadius: '30px'}}>
                        <div key={index} className='' style={{ marginLeft: '10px' }}>
                            <p key={index} className='Text-name'>Name : {img.filename}</p>
                            <p key={index} className='Text-result'>Result : {img.prediction}</p>
                        </div>
                        </div>
                    </div>
                ))} */}
                {images.map((item, index) => (
                    <div key={index} className='Div-img-box' style={{ display: 'flex', flexDirection: 'column', justifyContent: 'space-between', marginRight: '10px' }}>
                        <img src={item.src} alt={item.name} className='Image-style' />
                        <div key={index} style={{ background: 'gray', borderBottomLeftRadius: '30px', borderBottomRightRadius: '30px' }}>
                            <div key={index} className='' style={{ marginLeft: '10px' }}>
                                <p key={index} className='Text-name'>Name : {item.name}</p>
                                <p key={index} className='Text-result'>Result :</p>
                            </div>
                        </div>
                    </div>
                ))}
            </div>


            <div className='Div-back'>
                <Link to="/show" className="logo">
                    <img src={logo} alt="" className="Back-ele" />
                </Link>
                <p className='Back-text'>back</p>
            </div>
        </div>


    );
};

export default ShowST;
